import React, { Component } from "react";
import Navbar from "./navbar";
import Event from "./event";
// import EventDetail from "./eventDetail";

export default class Home extends Component {
  state = {
    events: [],
    searchTerm: ""
  };
  componentDidMount() {
    const token = localStorage.getItem("access_token");
    if (!token) {
      this.props.history.push("/login");
    } else {
      fetch("http://localhost:3000/get-all-events", {
        headers: { Authorization: token }
      })
        .then(res => res.json())
        .then(data => this.setState({ events: [...data] }));
    }
  }
  submitForm = (searchTerm) => {
    this.setState({
      searchTerm
    })
  }

  filterEvents = () => this.state.events.filter(p => p.name.toLocaleLowerCase().includes(this.state.searchTerm.toLocaleLowerCase()))

  // logout = e => {
  //   e.preventDefault();
  //   this.props.history.push("/login");
  // };

  logout = e => {
    e.preventDefault();
    localStorage.removeItem("access_token");
    this.props.history.push("/login");
  };

  render() {
    const { events } = this.state;
    const all = events.map((event, key) => (
      <Event
        name={event.name}
        location={event.location}
        description={event.description}
        id={event.id}
        imageurl={event.image_url}
      />
    ));

    return (
      <div>
        <Navbar logout={this.logout} />
        <br />
        <br />
        <br />
        <div className="container">
          <h3> All Events</h3>
          {all}
        </div>
      </div>
    );
  }
}
