    getEvent = id => {
      const token = localStorage.getItem("access_token");
      fetch(`http://localhost:3000/events/${id}`, {
        headers: { Authorization: token }
      })
        .then(resp => resp.json())
        .then(resp => {
          this.setState({
            event_name: resp.name
          });
        })
        this.getEventName
    };

  getEventName = id => {
    // debugger;
    myEvent = events.select(event => event.id == id);
    return myEvent.name;
  };

  render() {
    const { event_id, ticket_class, price, description } = this.props;

    return (
      <div
        className="card p-6 mb-2 bg-secondary text-white"
        style={{ margin: "1rem" }}
        key={event_id}
      >
        <div className="card-header">Event: {this.getEvent(event_id)}</div>
        <div className="card-body">
          Ticket Tier: {ticket_class}
          <br />
          Ticket description: {description}
