import React, { Component } from "react";
import Navbar from "./navbar";
import { navigate } from "@reach/router";
import axios from "axios";
import Event from "./event";

export default class Profile extends Component {
  state = {
    events: [],
    name: "",
    description: "",
    image_url: "",
    date_time: "",
    location: "",
    update: false,
    user: {},
    ticketCounter: 0,
    tickets: {}
  };
  componentDidMount() {
    const token = localStorage.getItem("access_token");
    if (!token) {
      this.props.history.push("/login");
    } else {
      this.fetchMyEvents();
    }
  }

  fetchMyEvents = () => {
    const token = localStorage.getItem("access_token");

    fetch("http://localhost:3000/events", { headers: { Authorization: token } })
      .then(res => res.json())
      .then(res => {
        console.log(res.data);
        this.setState(
          {
            events: res.data
          },
          () => {
            this.getUser();
          }
        );
      });
  };

  handlechange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  getUser = () => {
    const token = localStorage.getItem("access_token");
    fetch(`http://localhost:3000/user-info`, {
      headers: { Authorization: token }
    })
      .then(res => res.json())
      .then(res => {
        console.log(res.data);
        this.setState({
          user: res.data
        });
      });
  };

  createNewEvent = e => {
    e.preventDefault();
    const { name, description, date_time, location, image_url } = this.state;
    console.log(name, description, date_time, location, image_url);
    const token = localStorage.getItem("access_token");
    console.log(token);
    const self = this;
    axios({
      method: "post",
      url: "http://localhost:3000/events",
      data: {
        name: name,
        description: description,
        location: location,
        date_time: date_time,
        image_url: image_url
      },
      headers: { Authorization: token }
    }).then(function(response) {
      self.setState({
        name: "",
        description: "",
        image_url: "",
        date_time: "",
        location: ""
      });
      self.fetchMyEvents();
    });
  };

  logout = e => {
    e.preventDefault();
    localStorage.removeItem("access_token");
    this.props.history.push("/login");
  };

  deleteEvent = (e, id) => {
    console.log("12");
    e.preventDefault();
    const token = localStorage.getItem("access_token");
    axios
      .delete(`http://localhost:3000/events/${id}`, {
        headers: { Authorization: token }
      })
      .then(res => {});
    window.location.reload();
  };

  updateState = (e, id) => {
    const { events } = this.state;
    console.log(events);
    const filterEvent = events.filter(event => event.id === id);
    const updateEvent = filterEvent[0];
    this.setState({
      name: updateEvent.name,
      location: updateEvent.location,
      description: updateEvent.description,
      image_url: updateEvent.image_url,
      date_time: updateEvent.date_time,
      update: true,
      updateEventId: id
    });
  };

  updateEvent = e => {
    e.preventDefault();
    const self = this;
    const {
      name,
      location,
      description,
      image_url,
      date_time,
      updateEventId
    } = this.state;
    const token = localStorage.getItem("access_token");
    axios({
      method: "put",
      url: `http://localhost:3000/events/${updateEventId}`,
      data: {
        name: name,
        description: description,
        location: location,
        date_time: date_time,
        image_url: image_url
      },
      headers: { Authorization: token }
    }).then(function(response) {
      self.setState({
        name: "",
        description: "",
        image_url: "",
        date_time: "",
        location: "",
        update: false
      });
      self.fetchMyEvents();
    });
  };

  cancelUpdate = () => {
    this.setState({
      name: "",
      description: "",
      image_url: "",
      date_time: "",
      location: "",
      update: false
    });
  };
  render() {
    const {
      events,
      name,
      description,
      date_time,
      location,
      image_url,
      update,
      user
    } = this.state;
    let myEvents = [];
    myEvents = events.map((event, key) => (
      <>
        <Event
          name={event.name}
          location={event.location}
          description={event.description}
          id={event.id}
          isEdit={true}
          deleteEvent={this.deleteEvent}
          updateState={this.updateState}
        />
        <br />
      </>
    ));
    return (
      <div>
        <Navbar logout={this.logout} />
        <br />
        <br />

        <div className="container">
          <h2>Welcome! {user && user.username}</h2>
          <p>{user && user.primary_location}</p>
          <hr />
          <br />
          <br />

          <div className="row">
            <div className="col">
              <h3>My events</h3>
              {myEvents ? myEvents : <h3>No event yet</h3>}
            </div>
            <div className="col">
              <div className="container">
                <div className="row">
                  <div className="col">
                    {!update ? <h3>Create new one</h3> : <h3>Update form</h3>}
                    <br />
                    <form>
                      <div className="form-group">
                        <input
                          type="text"
                          className="form-control"
                          aria-describedby="emailHelp"
                          placeholder="Name"
                          name="name"
                          value={name}
                          onChange={this.handlechange}
                        />
                      </div>
                      <div className="form-group">
                        <input
                          type="text"
                          className="form-control"
                          aria-describedby="emailHelp"
                          placeholder="Location"
                          name="location"
                          value={location}
                          onChange={this.handlechange}
                        />
                      </div>
                      <div className="form-group">
                        <input
                          type="text"
                          className="form-control"
                          aria-describedby="emailHelp"
                          placeholder="Time-date"
                          name="date_time"
                          value={date_time}
                          onChange={this.handlechange}
                        />
                      </div>
                      <div className="form-group">
                        <input
                          type="text"
                          className="form-control"
                          aria-describedby="emailHelp"
                          placeholder="Description"
                          name="description"
                          value={description}
                          onChange={this.handlechange}
                        />
                      </div>
                      <div className="form-group">
                        <input
                          type="text"
                          className="form-control"
                          aria-describedby="emailHelp"
                          placeholder="Image Url"
                          name="image_url"
                          value={image_url}
                          onChange={this.handlechange}
                        />
                      </div>
                      {!update ? (
                        <button
                          className="btn btn-outline-success"
                          onClick={this.createNewEvent}
                        >
                          Create
                        </button>
                      ) : (
                        <span>
                          <button
                            className="btn btn-outline-success"
                            style={{ margin: "1rem" }}
                            onClick={this.updateEvent}
                          >
                            Update
                          </button>
                          <button
                            className="btn btn-outline-success"
                            onClick={this.cancelUpdate}
                          >
                            cancel
                          </button>
                        </span>
                      )}
                    </form>
                  </div>
                  <div className="col-sm"></div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
