import React, { Component } from "react";
import { Link } from "react-router-dom";

export default class Ticket extends Component {
  state = {
    event_name: ""
  };
  componentDidMount() {
    console.log(this.props);
  }

  getEventName = id => {
    const token = localStorage.getItem("access_token");
    fetch(`http://localhost:3000/events/${id}`, {
      headers: { Authorization: token }
    })
      .then(resp => resp.json())
      .then(resp => {
        this.setState({
          event_name: resp.name
        });
      })
    .then(() => {console.log(this.state.event_name)})
  };

  render() {
    const { event_id, ticket_class, price, description } = this.props;

    return (
      <div
        className="card p-6 mb-2 bg-secondary text-white"
        style={{ margin: "1rem" }}
        key={event_id}
      >
        <div className="card-header">Event: {this.getEventName(event_id)}</div>
        <div className="card-body">
          Ticket Tier: {ticket_class}
          <br />
          Ticket description: {description}
          <br />
          Ticket price: Â£{price}
          <br />
        </div>
      </div>
    );
  }
}
