import React, { Component } from "react";
import Navbar from "./navbar";

export default class EventDetail extends Component {
  state = {
    event: {}
  };
  componentDidMount() {
    const token = localStorage.getItem("access_token");
    if (!token) {
      // this.props.history.push('/login')
    }
    const {
      match: { params }
    } = this.props;
    this.fetchEventDetail(params.id);
    this.fetchAllTickets(params.id);
  }
  logout = e => {
    e.preventDefault();
    localStorage.removeItem("access_token");
    this.props.history.push("/login");
  };

  fetchEventDetail = id => {
    const token = localStorage.getItem("access_token");

    fetch(`http://localhost:3000/events/${id}`, {
      headers: { Authorization: `bearer ${token}` }
    })
      .then(res => res.json())
      .then(res => {
        console.log(res);
        this.setState({
          event: res
        });
      });
  };

  fetchAllTickets = id => {
    const token = localStorage.getItem("access_token");
    fetch(`http://localhost:3000/events/${id}/tickets`, {
      headers: { Authorization: `bearer ${token}` }
    })
      .then(res => res.json())
      .then(res => console.log(res))
      .then(res => {
        this.setState({
          tickets: res
        });
      });
  };
  render() {
    const { event, tickets } = this.state;
    return (
      <div>
        <Navbar logout={this.logout} />
        <br />
        <br />
        <br />
        <div className="container">
          <div className="row">
            <div className="col-md-4">
              {event && (
                <img
                  src={event.image_url}
                  alt="12"
                  style={{ float: "right" }}
                />
              )}
            </div>
            <div className="col">
              {event && (
                <>
                  <h3>
                    <strong>{event.name}</strong>
                  </h3>
                  <h4>Description: {event.description}</h4>
                  <h4>Location: {event.location}</h4>
                  <h4>Date time: {event.date_time}</h4>
                  <hr />
                </>
              )}
              <hr />
              {tickets &&
                tickets.map(ticket => {
                  return (
                    <div
                      class="p-3 mb-2 bg-secondary text-white"
                      style={{ margin: "1rem" }}
                    >
                      Ticket class: {ticket.ticket_class}
                      <br />
                      Ticket description: {ticket.description}
                      <br />
                      Ticket availability: {ticket.availability}
                      <br />
                      Ticket price: Â£{ticket.price}
                      <br />
                    </div>
                  );
                })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
